mapper.select:
SELECT



FROM tushu  a



        and a.id in

        #{item}



        and a.tushu_name like CONCAT('%',#{params.tushuName},'%')


        and a.tushu_uuid_number like CONCAT('%',#{params.tushuUuidNumber},'%')


        and a.tushu_zuozhe like CONCAT('%',#{params.tushuZuozhe},'%')


        and a.tushu_types = #{params.tushuTypes}


        and a.tushu_content like CONCAT('%',#{params.tushuContent},'%')


          and UNIX_TIMESTAMP(a.chuban_time) >= UNIX_TIMESTAMP(#{params.chubanTimeStart}) 


          and UNIX_TIMESTAMP(a.chuban_time) <= UNIX_TIMESTAMP(#{params.chubanTimeEnd}) 


          and a.tushu_delete >= #{params.tushuDeleteStart}   


          and a.tushu_delete <= #{params.tushuDeleteEnd}   


        and a.tushu_delete = #{params.tushuDelete}


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 



order by a.${params.sort} ${params.order}
